import React from 'react';
import { motion } from 'framer-motion';

const Blog = () => {
  const blogPosts = [
    {
      title: "My Journey into Data Science",
      date: "September 15, 2025",
      excerpt: "Reflecting on my path from computer science student to data science enthusiast, and the key experiences that shaped my career direction.",
      readTime: "5 min read"
    },
    {
      title: "Building AI-Powered Solutions: Lessons from Hackathons",
      date: "August 28, 2025",
      excerpt: "Key insights and technical challenges from developing B√© T√≠, our award-winning Vietnamese cultural app at UEH BizTech Hackathon 2025.",
      readTime: "7 min read"
    },
    {
      title: "Power BI vs Traditional Analytics: A Comparison",
      date: "August 10, 2025",
      excerpt: "Exploring the advantages of modern business intelligence tools based on my experience winning The Next Analyst Challenge 4.",
      readTime: "6 min read"
    },
    {
      title: "The Future of NLP in Southeast Asia",
      date: "July 25, 2025",
      excerpt: "Discussing opportunities and challenges for natural language processing applications in Vietnamese and regional languages.",
      readTime: "8 min read"
    },
    {
      title: "From Intern to Data Professional: Skills That Matter",
      date: "July 12, 2025",
      excerpt: "Essential skills and mindset shifts I discovered during my 6-month internship at FPT Information System.",
      readTime: "4 min read"
    },
    {
      title: "Machine Learning in Production: Best Practices",
      date: "June 30, 2025",
      excerpt: "Technical insights on deploying ML models reliably, based on real-world project experience and industry best practices.",
      readTime: "10 min read"
    }
  ];

  return (
    <motion.div
      className="page-container"
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.6 }}
    >
      <h1 className="page-title">Blog & Insights</h1>

      <motion.p 
        style={{ textAlign: 'center', fontSize: '1.2rem', color: '#666', marginBottom: '3rem' }}
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.3 }}
      >
        Sharing my experiences in data science, AI, and technology
      </motion.p>

      <div className="blog-grid">
        {blogPosts.map((post, index) => (
          <motion.article
            key={index}
            className="blog-card"
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: index * 0.1, duration: 0.6 }}
            whileHover={{ y: -5 }}
          >
            <div className="blog-image">
              üìù
            </div>
            <div className="blog-content">
              <div className="blog-date">{post.date} ‚Ä¢ {post.readTime}</div>
              <h3 className="blog-title">{post.title}</h3>
              <p className="blog-excerpt">{post.excerpt}</p>
            </div>
          </motion.article>
        ))}
      </div>

      <motion.div 
        style={{ textAlign: 'center', marginTop: '3rem' }}
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.8 }}
      >
        <p style={{ color: '#666', fontStyle: 'italic' }}>
          More articles coming soon! Follow me on social media for updates.
        </p>
      </motion.div>
    </motion.div>
  );
};

export default Blog;
